package com.wy.repository;import java.util.Optional;import org.springframework.data.repository.CrudRepository;/** * 基于Spring Data Redis的客户端repository * * @author 飞花梦影 * @date 2024-11-07 09:33:44 * @git {@link https://github.com/dreamFlyingFlower} */public interface RedisClientRepository extends CrudRepository<RedisRegisteredClient, String> {	/**	 * 根据客户端Id查询客户端信息	 *	 * @param clientId 客户端id	 * @return 客户端信息	 */	Optional<RedisRegisteredClient> findByClientId(String clientId);}